import React, { useState, useEffect } from 'react';
import { Row, Col, Card, Badge, ListGroup } from 'react-bootstrap';
import { useSocket } from '../contexts/SocketContext';
import AgentNodeGraph from './AgentNodeGraph';

const AgentStatus = ({ addAlert }) => {
  const { connected, messages } = useSocket();
  const [recentMessages, setRecentMessages] = useState([]);

  useEffect(() => {
    const agentMessages = messages
      .filter(m => m.type === 'agent_message')
      .map(m => ({ ...m.data, id: m.data.message_id }));

    if (agentMessages.length > 0) {
      setRecentMessages(prev => [...agentMessages, ...prev].slice(0, 10));
    }
  }, [messages]);

  const getAgentIcon = (agentType) => {
    const icons = {
      DEMAND: 'üìä',
      SUPPLY: 'üì¶',
      LOGISTICS: 'üöö',
      NEGOTIATION: 'ü§ù'
    };
    return icons[agentType] || 'ü§ñ';
  };

  return (
    <div>
      <Row className="mb-4">
        <Col>
          <h2>Agent Status & Communication</h2>
          <p className="text-muted">Real-time monitoring of AI agents and their interactions</p>
        </Col>
      </Row>

      <Row>
        <Col md={7}>
          <Card style={{ height: '550px' }}>
            <Card.Header>Agent Interaction Graph</Card.Header>
            <Card.Body>
              <AgentNodeGraph messages={messages} />
            </Card.Body>
          </Card>
        </Col>
        
        <Col md={5}>
          <Card style={{ height: '550px' }}>
            <Card.Header>Communication Log</Card.Header>
            <Card.Body style={{ overflowY: 'auto' }}>
              <ListGroup variant="flush">
                {recentMessages.map(message => (
                  <ListGroup.Item key={message.id} className="bg-transparent px-0">
                    <div className="d-flex justify-content-between">
                      <div className="fw-bold">
                        {getAgentIcon(message.from_agent)} {message.from_agent} ‚Üí {getAgentIcon(message.to_agent)} {message.to_agent}
                      </div>
                      <small>{new Date(message.timestamp).toLocaleTimeString()}</small>
                    </div>
                    <div className="small text-muted mt-1">
                      {message.message_type}: {JSON.stringify(message.content).substring(0, 50)}...
                    </div>
                  </ListGroup.Item>
                ))}
              </ListGroup>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default AgentStatus;
