import React, { useState, useEffect } from 'react';
import { Row, Col, Card, Button } from 'react-bootstrap';
import MetricCard from './MetricCard';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, BarChart, Bar, Legend } from 'recharts';
import { useSocket } from '../contexts/SocketContext';

const Dashboard = ({ addAlert }) => {
  const { messages, sendMessage } = useSocket();
  const [metrics, setMetrics] = useState({
    totalInventoryValue: 125000,
    totalCarbonFootprint: 2450,
    serviceLevel: 0.94,
    optimizationCyclesCompleted: 42
  });

  const [demandData, setDemandData] = useState([]);
  const [inventoryData, setInventoryData] = useState([]);

  useEffect(() => {
    const now = new Date();
    setDemandData(
      Array(24).fill(0).map((_, i) => ({
        time: new Date(now.getTime() - (23 - i) * 60 * 60 * 1000).toLocaleTimeString(),
        demand: Math.random() * 100 + 50,
        forecast: Math.random() * 100 + 50
      }))
    );
    setInventoryData(
      Array(7).fill(0).map((_, i) => ({
        date: new Date(now.getTime() - (6 - i) * 24 * 60 * 60 * 1000).toLocaleDateString(),
        north: Math.random() * 1000 + 500,
        central: Math.random() * 1200 + 600,
        south: Math.random() * 800 + 400
      }))
    );
  }, []);

  useEffect(() => {
    messages.forEach(message => {
      if (message.type === 'system_metrics') {
        setMetrics(prev => ({ ...prev, ...message.data }));
      } else if (message.type === 'optimization_result') {
        addAlert('info', `Optimization completed for ${message.data.sku_id}`);
      }
    });
  }, [messages, addAlert]);

  const simulateDemandSpike = () => {
    sendMessage('simulate_demand_spike', { sku_id: 'SKU001', multiplier: 2.5 });
    addAlert('warning', 'Simulating demand spike for SKU001');
  };

  const simulateDelay = () => {
    sendMessage('simulate_delay', { supplier_id: 'SUP001', delay_days: 3 });
    addAlert('warning', 'Simulating supplier delay for SUP001');
  };

  return (
    <div>
      <Row className="mb-4 align-items-center">
        <Col>
          <h2>System Dashboard</h2>
          <p className="text-muted">Real-time inventory optimization and agent coordination</p>
        </Col>
        <Col xs="auto">
          <div className="d-flex gap-2">
            <Button variant="outline-warning" onClick={simulateDemandSpike}>Simulate Demand Spike</Button>
            <Button variant="outline-danger" onClick={simulateDelay}>Simulate Delay</Button>
          </div>
        </Col>
      </Row>

      <Row className="mb-4">
        <Col md={3}><MetricCard label="Total Inventory Value" value={`$${metrics.totalInventoryValue.toLocaleString()}`} icon="ðŸ’°" /></Col>
        <Col md={3}><MetricCard label="Carbon Footprint" value={`${metrics.totalCarbonFootprint}kg`} icon="ðŸŒ¿" /></Col>
        <Col md={3}><MetricCard label="Service Level" value={`${(metrics.serviceLevel * 100).toFixed(1)}%`} icon="ðŸŽ¯" /></Col>
        <Col md={3}><MetricCard label="Optimization Cycles" value={metrics.optimizationCyclesCompleted} icon="ðŸ”„" /></Col>
      </Row>

      <Row className="mb-4">
        <Col md={8}>
          <Card>
            <Card.Header>Demand vs Forecast (24h)</Card.Header>
            <Card.Body>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={demandData}>
                  <defs>
                    <linearGradient id="colorDemand" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8}/>
                      <stop offset="95%" stopColor="#8884d8" stopOpacity={0}/>
                    </linearGradient>
                    <linearGradient id="colorForecast" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="5%" stopColor="#82ca9d" stopOpacity={0.8}/>
                      <stop offset="95%" stopColor="#82ca9d" stopOpacity={0}/>
                    </linearGradient>
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="time" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Line type="monotone" dataKey="demand" stroke="#8884d8" strokeWidth={2} dot={false} />
                  <Line type="monotone" dataKey="forecast" stroke="#82ca9d" strokeWidth={2} dot={false} />
                </LineChart>
              </ResponsiveContainer>
            </Card.Body>
          </Card>
        </Col>
        <Col md={4}>
          <Card>
            <Card.Header>Warehouse Utilization</Card.Header>
            <Card.Body className="d-flex justify-content-around align-items-center">
                {/* Replace with custom radial progress bars if time permits */}
                <div>North: 78%</div>
                <div>Central: 65%</div>
                <div>South: 92%</div>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <Row>
        <Col>
          <Card>
            <Card.Header>Inventory Levels by Warehouse (7 days)</Card.Header>
            <Card.Body>
              <ResponsiveContainer width="100%" height={250}>
                <BarChart data={inventoryData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey="north" fill="#8884d8" />
                  <Bar dataKey="central" fill="#82ca9d" />
                  <Bar dataKey="south" fill="#ffc658" />
                </BarChart>
              </ResponsiveContainer>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default Dashboard;
