import React from 'react';
import { Badge } from 'react-bootstrap';

const SidebarLink = ({ label, icon, isActive, onClick }) => {
  return (
    <button 
      className={`sidebar-link ${isActive ? 'active' : ''}`}
      onClick={onClick}
    >
      <span className="sidebar-icon" aria-hidden>{icon}</span>
      <span>{label}</span>
    </button>
  );
};

const Layout = ({ activeTab, setActiveTab, children }) => {
  return (
    <div className="app-shell">
      <aside className="app-sidebar">
        <div className="sidebar-brand">
          <span className="brand-emoji">ðŸ¤–</span>
          <div className="brand-text">
            <div className="brand-title">Inventory Orchestrator</div>
            <div className="brand-sub">Autonomous Ops</div>
          </div>
        </div>

        <nav className="sidebar-nav">
          <SidebarLink 
            label="Dashboard" 
            icon="ðŸ“ˆ" 
            isActive={activeTab === 'dashboard'}
            onClick={() => setActiveTab('dashboard')} 
          />
          <SidebarLink 
            label="Agents" 
            icon="ðŸ¤" 
            isActive={activeTab === 'agents'}
            onClick={() => setActiveTab('agents')} 
          />
          <SidebarLink 
            label="Inventory" 
            icon="ðŸ“¦" 
            isActive={activeTab === 'inventory'}
            onClick={() => setActiveTab('inventory')} 
          />
          <SidebarLink 
            label="Sustainability" 
            icon="ðŸŒ¿" 
            isActive={activeTab === 'sustainability'}
            onClick={() => setActiveTab('sustainability')} 
          />
        </nav>

        <div className="sidebar-footer">
          <span className="status-dot" />
          <span className="status-text">System Online</span>
          <Badge bg="success" className="ms-auto">Live</Badge>
        </div>
      </aside>

      <main className="app-content">
        {children}
      </main>
    </div>
  );
};

export default Layout;
