import React, { useState, useEffect } from 'react';
import { Row, Col, Card, Button } from 'react-bootstrap';
import MetricCard from './MetricCard';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend, Label } from 'recharts';
import { useSocket } from '../contexts/SocketContext';

const CustomTooltip = ({ active, payload, label }) => {
  if (active && payload && payload.length) {
    return (
      <div className="custom-tooltip">
        <p className="label">{`${label}`}</p>
        <p className="intro" style={{color: payload[0].color}}>{`Demand : ${payload[0].value}`}</p>
        <p className="intro" style={{color: payload[1].color}}>{`Forecast : ${payload[1].value}`}</p>
      </div>
    );
  }
  return null;
};

const Dashboard = ({ addAlert }) => {
  const { messages, sendMessage } = useSocket();
  const [metrics, setMetrics] = useState({
    totalInventoryValue: 125000,
    totalCarbonFootprint: 2450,
    serviceLevel: 0.94,
    optimizationCyclesCompleted: 42
  });

  const [demandData, setDemandData] = useState([]);
  const [inventoryData, setInventoryData] = useState([]);

  useEffect(() => {
    const now = new Date();
    setDemandData(
      Array(24).fill(0).map((_, i) => ({
        time: new Date(now.getTime() - (23 - i) * 60 * 60 * 1000).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
        demand: (Math.random() * 80 + 60).toFixed(0),
        forecast: (Math.random() * 70 + 50).toFixed(0)
      }))
    );
    setInventoryData(
      Array(7).fill(0).map((_, i) => ({
        date: new Date(now.getTime() - (6 - i) * 24 * 60 * 60 * 1000).toLocaleDateString([], { month: 'short', day: 'numeric' }),
        north: (Math.random() * 800 + 400).toFixed(0),
        central: (Math.random() * 1000 + 500).toFixed(0),
        south: (Math.random() * 700 + 300).toFixed(0)
      }))
    );
  }, []);

  useEffect(() => {
    messages.forEach(message => {
      if (message.type === 'system_metrics') {
        setMetrics(prev => ({ ...prev, ...message.data }));
      } else if (message.type === 'optimization_result') {
        addAlert('info', `Optimization completed for ${message.data.sku_id}`);
      }
    });
  }, [messages, addAlert]);

  const simulateDemandSpike = () => {
    sendMessage('simulate_demand_spike', { sku_id: 'SKU001', multiplier: 2.5 });
    addAlert('warning', 'Simulating demand spike for SKU001');
  };

  const simulateDelay = () => {
    sendMessage('simulate_delay', { supplier_id: 'SUP001', delay_days: 3 });
    addAlert('warning', 'Simulating supplier delay for SUP001');
  };

  return (
    <div>
      <Row className="mb-4 align-items-center">
        <Col>
          <h2>System Dashboard</h2>
          <p className="text-muted">Real-time inventory optimization and agent coordination</p>
        </Col>
        <Col xs="auto">
          <div className="d-flex gap-2">
            <Button variant="outline-warning" onClick={simulateDemandSpike}>Simulate Demand Spike</Button>
            <Button variant="outline-danger" onClick={simulateDelay}>Simulate Delay</Button>
          </div>
        </Col>
      </Row>

      <Row className="mb-4">
        <Col md={3}><MetricCard label="Total Inventory Value" value={`$${metrics.totalInventoryValue.toLocaleString()}`} icon="💰" /></Col>
        <Col md={3}><MetricCard label="Carbon Footprint" value={`${metrics.totalCarbonFootprint}kg`} icon="🌿" /></Col>
        <Col md={3}><MetricCard label="Service Level" value={`${(metrics.serviceLevel * 100).toFixed(1)}%`} icon="🎯" /></Col>
        <Col md={3}><MetricCard label="Optimization Cycles" value={metrics.optimizationCyclesCompleted} icon="🔄" /></Col>
      </Row>

      <Row className="mb-4">
        <Col>
          <Card>
            <Card.Header>Demand vs Forecast (24h)</Card.Header>
            <Card.Body>
              <ResponsiveContainer width="100%" height={350}>
                <AreaChart data={demandData} margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                  <defs>
                    <linearGradient id="colorDemand" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="5%" stopColor="var(--cyan-400)" stopOpacity={0.8}/>
                      <stop offset="95%" stopColor="var(--cyan-400)" stopOpacity={0}/>
                    </linearGradient>
                    <linearGradient id="colorForecast" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="5%" stopColor="var(--fuchsia-500)" stopOpacity={0.8}/>
                      <stop offset="95%" stopColor="var(--fuchsia-500)" stopOpacity={0}/>
                    </linearGradient>
                     <filter id="glow" x="-50%" y="-50%" width="200%" height="200%">
                      <feGaussianBlur stdDeviation="3" result="coloredBlur" />
                      <feMerge>
                        <feMergeNode in="coloredBlur" />
                        <feMergeNode in="SourceGraphic" />
                      </feMerge>
                    </filter>
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="time" />
                  <YAxis>
                    <Label value="Units" angle={-90} position="insideLeft" style={{ textAnchor: 'middle', fill: '#94a3b8' }} />
                  </YAxis>
                  <Tooltip content={<CustomTooltip />} />
                  <Legend />
                  <Area type="monotone" dataKey="demand" stroke="var(--cyan-400)" fill="url(#colorDemand)" strokeWidth={2} filter="url(#glow)" />
                  <Area type="monotone" dataKey="forecast" stroke="var(--fuchsia-500)" fill="url(#colorForecast)" strokeWidth={2} filter="url(#glow)" />
                </AreaChart>
              </ResponsiveContainer>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default Dashboard;
